import { Component, OnInit } from '@angular/core';
import { HttpServiceService } from '../services/http-service.service';

@Component({
  selector: 'app-productlist',
  templateUrl: './productlist.component.html',
  styleUrls: ['./productlist.component.css']
})
export class ProductlistComponent implements OnInit {
  endpoint = "http://localhost:8080/Product/search";
  form: any = {
    data: {},
    pageNo:0,
    searchParams:{},
    list:[]
  }

  inputerror: { [key: string]: string } = {}; // Define inputerror with appropriate typing
  message = '';

  constructor(private httpService: HttpServiceService) { }

  ngOnInit() {
      this.search();
    }


  search() {
    var self = this;
    this.httpService.post(self.endpoint + "/" + self.form.pageNo,self.form.searchParams , function(res:any){
      if(res.result.inputerror){
        self.inputerror = res.result.inputerror;
      }
      if(res.result.DATA){
        self.form.list=res.result.DATA;
      }
    });
  }

}
