import { Component, OnInit } from '@angular/core';
import { HttpServiceService } from '../services/http-service.service';

@Component({
  selector: 'app-productlist',
  templateUrl: './productlist.component.html',
  styleUrls: ['./productlist.component.css']
})
export class ProductlistComponent implements OnInit {
  endpoint = "http://localhost:8080/Product/search";
  categoriesEndpoint = "http://localhost:8080/Product/preload"; // Endpoint to get categories
  form: any = {
    data: {},
    pageNo: 0,
    searchParams: { name: '', category: '' },
    list: []
  };

  inputerror: { [key: string]: string } = {}; // Define inputerror with appropriate typing
  message = '';
  categories: string[] = [];

  constructor(private httpService: HttpServiceService) { }

  ngOnInit() {

    this.search(); // Load initial data
  }


  search() {
    const searchParams = {
      name: this.form.searchParams.name.trim(), // Trim whitespace
      category: this.form.searchParams.category
    };

    this.httpService.post(this.endpoint + "/" + this.form.pageNo, searchParams, (res: any) => {
      if (res.result.inputerror) {
        this.inputerror = res.result.inputerror;
      }
      if (res.result.DATA) {
        this.form.list = res.result.DATA;
      }
    });
  }

}
