import { Component } from '@angular/core';

@Component({
  selector: 'app-shooping-cart',
  templateUrl: './shooping-cart.component.html',
  styleUrls: ['./shooping-cart.component.css']
})
export class ShoopingCartComponent {

  products: Product[] = [];
  filteredProducts: Product[] = [];
  categories: string[] = ['Electronics', 'Books', 'Clothing'];
  selectedCategory: string = 'All';
  searchKeyword: string = '';

  constructor(private productService: ProductService) { }

  ngOnInit(): void {
    this.productService.getProducts().subscribe(data => {
      this.products = data;
      this.filteredProducts = data;
    });
  }

  filterByCategory(): void {
    if (this.selectedCategory === 'All') {
      this.filteredProducts = this.products;
    } else {
      this.productService.getProductsByCategory(this.selectedCategory).subscribe(data => {
        this.filteredProducts = data;
      });
    }
  }

  searchProducts(): void {
    if (this.searchKeyword) {
      this.productService.searchProducts(this.searchKeyword).subscribe(data => {
        this.filteredProducts = data;
      });
    } else {
      this.filteredProducts = this.products;
    }
  }
}
