import { Component } from '@angular/core';
import { AuthInterceptorService } from '../services/auth-interceptor.service';
import { HttpServiceService } from '../services/http-service.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  endpoint = "http://localhost:8080/Product";
  form:any = {
    data: {},
    error: false,
    name: '',
    description: '',
    price: '',
    dateOfPurchase: '',
    category: '',
    message: ''
  };

  inputerror: { [key: string]: string } = {}; // Define inputerror with appropriate typing
  message = '';

  constructor(private authService: AuthInterceptorService, private httpService: HttpServiceService, private router: Router, private route: ActivatedRoute) { 
    this.route.params.subscribe((params: any) => {
      this.form.data.id = params["id"];
      console.log(this.form.data.id)
    })
  }

  ngOnInit(): void { }
  onSubmit() {
    var self = this;
    this.httpService.post(this.endpoint + "/save", this.form.data, function (res: any) {
      self.inputerror = {};

      if (res.result.inputerror) {
        self.inputerror = res.result.inputerror;
      }

      if (res.success && res.result.data != null) {
        self.form.data = res.result.data;
        self.form.data.message = "Product added successfully!"; // Set success message
        self.message = "Product added successfully!"; // Set success message
      } 
      if(!res.result.data){
        self.form.data.message = "Failed to add product.";
      }
    });
  }


}
<h1>hello</h1>
<div class="login-container">
    <div class="login-box">
        <h2 class="login-title">Product Details</h2>

        <form (ngSubmit)="onSubmit()">
            <!-- Display general error message if login fails -->
            <div *ngIf="form.message != null" class="alert alert-danger">{{ form.data.message }}</div>

            <div class="form-group">
                <label for="name">name</label>
                <input type="text" name="name" id="name" [(ngModel)]="form.data.name" required class="form-control">
                <!-- Display loginId error -->
                <span *ngIf="inputerror['name']" class="text-danger">{{ inputerror['name'] }}</span>
            </div>

            <div class="form-group">
                <label for="description">description</label>
                <input type="text" name="description" id="description" [(ngModel)]="form.data.description" required class="form-control">
                <!-- Display loginId error -->
                <span *ngIf="inputerror['description']" class="text-danger">{{ inputerror['description'] }}</span>
            </div>
            <div class="form-group">
                <label for="price">price</label>
                <input type="text" name="price" id="price" [(ngModel)]="form.data.price" required class="form-control">
                <!-- Display loginId error -->
                <span *ngIf="inputerror['price']" class="text-danger">{{ inputerror['price'] }}</span>
            </div>

            <div class="form-group">
                <label for="dateOfPurchase">dateOfPurchase</label>
                <input type="date" name="dateOfPurchase" id="dateOfPurchase" [(ngModel)]="form.data.dateOfPurchase" required class="form-control">
                <!-- Display loginId error -->
                <span *ngIf="inputerror['dateOfPurchase']" class="text-danger">{{ inputerror['dateOfPurchase'] }}</span>
            </div>

            <div class="form-group">
                <label for="category">category</label>
                <input type="type" name="category" id="category" [(ngModel)]="form.data.category" required class="form-control">
                <!-- Display loginId error -->
                <span *ngIf="inputerror['category']" class="text-danger">{{ inputerror['category'] }}</span>
            </div>
            <!-- <button (click)="onSubmit()">save</button>  -->
            <button   class="btn btn-primary btn-block">Save</button>
        </form>
    </div>
</div>

<!-- CSS Styles -->
<style>
    .login-container {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100vh; /* Full height to center vertically */
        background-color: #f4f7f9; /* Light background for the whole page */
    }

    .login-box {
        width: 400px; /* Fixed width for consistency */
        padding: 30px;
        border: 1px solid #ddd; /* Light border */
        border-radius: 8px; /* Rounded corners */
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); /* Subtle shadow for depth */
        background-color: #fff; /* White background for contrast */
    }

    .login-title {
        margin-bottom: 20px;
        text-align: center;
        color: #333; /* Dark color for better readability */
        font-weight: bold;
    }

    .form-group {
        margin-bottom: 20px; /* Space between fields */
    }

    .form-control {
        border: 1px solid #ccc; /* Border around input fields */
        border-radius: 4px; /* Slightly rounded corners */
        padding: 10px;
        font-size: 16px;
        transition: border-color 0.3s ease; /* Smooth border color change */
    }

    .form-control:focus {
        border-color: #007bff; /* Highlighted border on focus */
        box-shadow: 0 0 8px rgba(0, 123, 255, 0.2); /* Subtle glow effect */
    }

    .btn-primary {
        background-color: #007bff; /* Primary button color */
        border: none;
        padding: 10px;
        font-size: 16px;
        font-weight: bold;
        cursor: pointer;
        border-radius: 4px;
        transition: background-color 0.3s ease; /* Smooth transition */
    }

    .btn-primary:hover {
        background-color: #0056b3; /* Darker shade on hover */
    }

    .text-danger {
        font-size: 14px; /* Slightly smaller font for error messages */
        color: #d9534f; /* Red color for error messages */
        margin-top: 5px;
        display: block; /* Display error messages on a new line */
    }
</style>
