import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  form: any = {
    name: ' ',
    data: {}
  }
  isLogin() {
    let check = localStorage.getItem('name');
    if (check != "null" && check != null) {
      this.form.data.name = localStorage.getItem('name');
      return true;

    } else {
      return false;
    }
  }

}
<div *ngIf="isLogin()">
  <nav class="navbar navbar-expand-lg navbar-dark bg-primary shadow-lg">
    <a class="navbar-brand" href="#">Dashboard</a>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav ml-auto">
        <!-- Product Dropdown -->
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            Product
          </a>
          <div class="dropdown-menu" aria-labelledby="navbarDropdown">
            <a class="dropdown-item" routerLink="/products">Add Product</a>
            <a class="dropdown-item" routerLink="/productlist">Product List</a>
          </div>
        </li>
        <!-- Orders Link -->
        <li class="nav-item">
          <a class="nav-link" routerLink="/orders">Orders</a>
        </li>
        <!-- Wishlist Link -->
        <li class="nav-item">
          <a class="nav-link" routerLink="/wishlist">Wishlist</a>
        </li>
        <!-- Shopping Cart Link -->
        <li class="nav-item">
          <a class="nav-link" routerLink="/shopping-cart">Cart</a>
        </li>
      </ul>
    </div>
  </nav>
</div>

<div *ngIf="!isLogin()">
  <div class="container text-center mt-5">
    <h1 class="display-3 font-weight-bold">Welcome to Your Dashboard</h1>
    <p class="lead mb-4">Please <a class="btn btn-primary btn-lg" routerLink="/login">Log In</a> to access your account.</p>
  </div>
</div>
