import { Component, OnInit } from '@angular/core';
import { HttpServiceService } from '../services/http-service.service';

@Component({
  selector: 'app-productlist',
  templateUrl: './productlist.component.html',
  styleUrls: ['./productlist.component.css']
})
export class ProductlistComponent implements OnInit {
  endpoint = "http://localhost:8080/Product/search";
  categoriesEndpoint = "http://localhost:8080/Product/preload"; // Endpoint to get categories
  form: any = {
    data: {},
    pageNo: 0,
    searchParams: { name: '', category: '' },
    list: []
  };

  inputerror: { [key: string]: string } = {}; // Define inputerror with appropriate typing
  message = '';
  categories: string[] = [];

  constructor(private httpService: HttpServiceService) { }

  ngOnInit() {
    this.loadCategories();
    this.search(); // Load initial data
  }
  loadCategories() {
    this.httpService.get(this.categoriesEndpoint, (res: any) => {
      if (res.result && res.result.categories) {
        this.form.categories = res.result.categories; // Update categories
      }
    });
  }

  search() {
    const searchParams = {
      name: this.form.searchParams.name.trim(), // Trim whitespace
      category: this.form.searchParams.category
    };

    this.httpService.post(this.endpoint + "/" + this.form.pageNo, searchParams, (res: any) => {
      if (res.result.inputerror) {
        this.inputerror = res.result.inputerror;
      }
      if (res.result.DATA) {
        this.form.list = res.result.DATA;
      }
    });
  }
}
<div class="product-list">
  <h2>Product List</h2>

  <!-- Search Bar -->
  <div class="search-container">
    <input
      type="text"
      [(ngModel)]="searchKeyword"
      (input)="searchProducts()"
      placeholder="Search products..."
    />
  </div>

  <!-- Product Grid -->
  <div class="product-grid">
    <div *ngFor="let m of form.list" class="product-card">
      <img
        [src]="m.imageUrl || 'assets/default-image.jpg'"
        alt="{{ m.name }}"
        class="product-image"
      />
      <div class="product-info">
        <h3 class="product-name">{{ m.name || 'N/A' }}</h3>
        <p class="product-description">{{ m.description || 'N/A' }}</p>
        <p class="product-price">{{ m.price | currency: 'INR' }}</p>
        <p class="product-date">
          Purchased on: {{ m.dateOfPurchase | date: 'shortDate' }}
        </p>
        <p class="product-category">Category: {{ m.category || 'N/A' }}</p>
      </div>
    </div>

    <!-- No Products Available Message -->
    <div *ngIf="form.list.length === 0" class="no-products">
      No products available
    </div>
  </div>
</div>
