import { Component } from '@angular/core';
import { AuthInterceptor } from '../auth-interceptor.service';
import { HttpServiceService } from '../services/http-service.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  endpoint = "http://localhost:8080/Product";
  form = {
    data: '',
    error: false,
    name: '',
    description: '',
    price: '',
    dateOfPurchase: '',
    category: ''
  };

  inputerror: { [key: string]: string } = {}; // Define inputerror with appropriate typing
  message = '';

  constructor(private authService: AuthService, private httpService: HttpServiceService, private router: Router, private rout: ActivatedRoute) { }

  ngOnInit(): void { }

  onSubmit() {
    var _self = this;
    this.httpService.post(this.endpoint + "/save", this.form.data, function (res: any) {

      _self.inputerror={};
      if(res.result.inputerror){
        _self.inputerror=res.result.inputerror;
      }
    });
  }

}
