import { Component } from '@angular/core';
import { HttpServiceService } from '../services/http-service.service';

@Component({
  selector: 'app-productlist',
  templateUrl: './productlist.component.html',
  styleUrls: ['./productlist.component.css']
})
export class ProductlistComponent {
  endpoint = "http://localhost:8080/User/search";
  form: any = {
    data: {},
    pageNo:'',
    searchParams:{}
  }

  inputerror: { [key: string]: string } = {}; // Define inputerror with appropriate typing
  message = '';

  constructor(private httpService: HttpServiceService) { }

  search() {
    var self = this;
    this.httpService.post(self.endpoint + "/" + self.form.pageNo,this.form.searchParam , function(res:any){
      if(res.result.inputerror){
        self.inputerror = res.result.inputerror;
      }
      if(res.result.data){
        self.form.data=res.result.data;
      }
    });
  }
}
<app-navbar></app-navbar>

  <table *ngIf="products.length > 0" class="product-table">
    <thead>
      <tr>
        <th>Name</th>
        <th>Description</th>
        <th>Price</th>
        <th>Date of Purchase</th>
        <th>Category</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let product of form.searchParams">
        <td>{{ product.name }}</td>
        <td>{{ product.description }}</td>
        <td>{{ product.price | currency }}</td>
        <td>{{ product.dateOfPurchase | date }}</td>
        <td>{{ product.category }}</td>
      </tr>
    </tbody>
  </table>
