import { AuthService } from '../services/auth.service';
import { HttpServiceService } from '../services/http-service.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  endpoint = " http://localhost:8080/Auth";

  form = {
    error:false,
    loginId : '',
    password : ''
  };

  inputerror = {};
  message ='';
constructor(private authService: AuthService , private httpService : HttpServiceService , private router:Router , private rout:ActivatedRoute){

}

onLogin(){
  var _self = this;
  this.httpService.post(this.endpoint + "/login" , this.form , function (res:any){
    _self.inputerror={};
