import { Component } from '@angular/core';

@Component({
  selector: 'app-shooping-cart',
  templateUrl: './shooping-cart.component.html',
  styleUrls: ['./shooping-cart.component.css']
})
export class ShoopingCartComponent {import { Component, OnInit } from '@angular/core';
import { HttpServiceService } from '../services/http-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-shopping-cart',
  templateUrl: './shopping-cart.component.html',
  styleUrls: ['./shopping-cart.component.css']
})
export class ShoppingCartComponent implements OnInit {
  cartItems: any[] = [];
  userId: number | null = null;
  message: string = '';

  constructor(private httpService: HttpServiceService, private router: Router) { }

  ngOnInit() {
    this.getUserId();
    this.loadCartItems();
  }

  getUserId() {
    const userId = localStorage.getItem('userId');
    this.userId = userId ? +userId : null; // Convert string to number
  }

  loadCartItems() {
    if (!this.userId) {
      this.message = 'User not logged in.';
      return;
    }

    this.httpService.get(`http://localhost:8080/Cart/user/${this.userId}`, (res: any) => {
      if (res.success && res.result) {
        this.cartItems = res.result;
      } else {
        this.message = 'No items found in the cart.';
      }
    });
  }

  removeItem(productId: number) {
    // Implement functionality to remove an item from the cart
    console.log('Remove item with ID:', productId);
    // Call your API to remove the item from the cart
  }
}

}
