import { Component, OnInit } from '@angular/core';
import { AuthService } from '../services/auth.service';
import { HttpServiceService } from '../services/http-service.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  endpoint = "http://localhost:8080/Auth";

  form = {
    error: false,
    loginId: '',
    password: ''
  };

  inputerror: { [key: string]: string } = {}; // Define inputerror with appropriate typing
  message = '';

  constructor(private authService: AuthService, private httpService: HttpServiceService, private router: Router, private rout: ActivatedRoute) {}

  ngOnInit(): void {}

  onLogin() {
    var _self = this;
    this.httpService.post(this.endpoint + "/login", this.form, function (res: any) {
      _self.inputerror = {}; // Reset inputerror before setting new errors
      if (res.result && res.result.inputerror) {
        _self.inputerror = res.result.inputerror; // Assign errors from the backend to inputerror
      }
      if (res.success) {
        // Handle successful login
        _self.router.navigate(['/home']); // Example: navigate to home page
      } else {
        _self.message = 'Login failed. Please check your credentials.'; // Handle login failure
      }
    });
  }
}
<div class="login-container">
    <h2>Login</h2>
    <form (ngSubmit)="onLogin()">

        <div *ngIf="message" class="alert alert-danger mt-2">{{ message }}</div>

        <div class="form-group">
            <label for="loginId">LoginId</label>
            <input type="text" name="loginId" id="loginId" [(ngModel)]="form.loginId" required class="form-control">
            <!-- Display loginId error -->
            <span *ngIf="inputerror['loginId']" class="text-danger">{{ inputerror['loginId'] }}</span>
        </div>
        <div class="form-group">
            <label for="password">Password</label>
            <input type="password" name="password" id="password" [(ngModel)]="form.password" required class="form-control">
            <!-- Display password error -->
            <span *ngIf="inputerror['password']" class="text-danger">{{ inputerror['password'] }}</span>
        </div>
        <button type="submit" class="btn btn-primary">Login</button>
        <!-- Display general error message if login fails -->
        <div *ngIf="message" class="alert alert-danger mt-2">{{ message }}</div>
    </form>
</div>
