import { Component, OnInit } from '@angular/core';
import { HttpServiceService } from '../services/http-service.service';

@Component({
  selector: 'app-productlist',
  templateUrl: './productlist.component.html',
  styleUrls: ['./productlist.component.css']
})
export class ProductlistComponent implements OnInit {
  endpoint = "http://localhost:8080/Product/search";
  categoriesEndpoint = "http://localhost:8080/Product/preload"; // Endpoint to get categories
  form: any = {
    data: {},
    pageNo: 0,
    searchParams: { name: '', category: '' },
    list: []
  };

  inputerror: { [key: string]: string } = {}; // Define inputerror with appropriate typing
  message = '';
  categories: string[] = [];

  constructor(private httpService: HttpServiceService) { }

  ngOnInit() {
    this.loadCategories();
    this.search(); // Load initial data
  }
  loadCategories() {
    this.httpService.get(this.categoriesEndpoint, (res: any) => {
      if (res.result && res.result.categories) {
        this.form.categories = res.result.categories; // Update categories
      }
    });
  }

  search() {
    const searchParams = {
      name: this.form.searchParams.name.trim(), // Trim whitespace
      category: this.form.searchParams.category
    };

    this.httpService.post(this.endpoint + "/" + this.form.pageNo, searchParams, (res: any) => {
      if (res.result.inputerror) {
        this.inputerror = res.result.inputerror;
      }
      if (res.result.DATA) {
        this.form.list = res.result.DATA;
      }
    });
  }
}
<app-navbar></app-navbar>

<div class="container mt-4">
  <!-- Page Title -->
  <h1 class="mb-4 text-center">Product List</h1>

  <!-- Search and Filter Section -->
  <div class="mb-4">
    <form (ngSubmit)="search()">
      <div class="form-group">
        <label for="searchName">Search by Name:</label>
        <div class="input-group">
          <input type="text" class="form-control" [(ngModel)]="form.searchParams.name" placeholder="Enter product name"
            name="name">
          <div class="input-group-append">
            <button type="submit" class="btn btn-primary">Search</button>
          </div>
        </div>
      </div>

      <div class="form-group">
        <label for="categorySelect">Select Category:</label>
        <select id="categorySelect" class="form-control" [(ngModel)]="form.searchParams.category"
          (change)="search()">
          <option value="">All Categories</option>
          <option *ngFor="let category of form.Categories" [value]="category">{{ category }}</option>
        </select>
      </div>
    </form>
  </div>

  <!-- Product Table -->
  <table class="product-table">
    <thead>
      <tr>
        <th>Name</th>
        <th>Description</th>
        <th>Price</th>
        <th>Date of Purchase</th>
        <th>Category</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let m of form.list">
        <td>{{ m.name || 'N/A' }}</td>
        <td>{{ m.description || 'N/A' }}</td>
        <td>{{ m.price | currency:'INR' }}</td>
        <td>{{ m.dateOfPurchase | date:'shortDate' }}</td>
        <td>{{ m.category || 'N/A' }}</td>
      </tr>
      <tr *ngIf="form.list.length === 0">
        <td colspan="5" class="text-center">No products available</td>
      </tr>
    </tbody>
  </table>
</div>

<app-footer></app-footer>
