import { Injectable } from '@angular/core';
import {
  HttpRequest,
  HttpHandler,
  HttpEvent,
  HttpInterceptor,
} from '@angular/common/http';
import { Observable } from 'rxjs';
import { HttpServiceService } from './http-service.service';

@Injectable()
export class AuthInterceptor implements HttpInterceptor {
  constructor(private httpService: HttpServiceService) {}

  intercept(
    request: HttpRequest<any>,
    next: HttpHandler
  ): Observable<HttpEvent<any>> {
    // Skip adding Authorization header for login and signup requests
    if (request.url.includes('/login') || request.url.includes('/signup')) {
      return next.handle(request);
    }

    // Retrieve token from storage
    const token = this.httpService.getToken();

    // If token exists, clone request and add Authorization header
    if (token) {
      const clonedRequest = request.clone({
        setHeaders: {
          Authorization: `Bearer ${token}`,
        },
      });
      return next.handle(clonedRequest);
    }

    // Proceed without adding token if no token found
    return next.handle(request);
  }
}
